$(document).ready(function(){$("body").on("click",".set_default",function(e){e.preventDefault();var t=$(this);$.get("/dealer/set_default_margin/"+$(this).data("id"),function(){t.closest(".table").find(".default_margin").hide(),t.closest(".table").find(".default_margin").next().show(),t.prev().show(),t.hide()})}),$("body").on("click",".delete",function(e){e.preventDefault();var t=$(this);confirm('Удалить наценку "'+t.parents(".table-row").find(".table-cell").first().html()+'"?')&&$.get("/dealer/delete_margin/"+$(this).data("id"),function(){t.parents(".table-row").remove()})})});